INSERT INTO `pages` (`id`, `name`, `data`) VALUES
(1, 'index', '<p style="text-align: left;"><span style="font-size: medium;"><strong>Welcome to the exciting past and present of an unusual small community in the heart of the Heartland!</strong></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;">This site opens our historical resources to the worldwide Internet and invites you to share in adding information, correcting our sources, and asking us for specific information you cannot find here. This site is like an organic history book &ndash;it continues to grow from the input and knowledge of anyone in the world. Almost all of our known historical resources will eventually be available to anyone with an Internet connection.</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><br /></span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;">This <strong>Home Page</strong> quickly introduces you to this site</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"> </span></p>\r\n<hr />\r\n<p>&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: medium;">In <strong>About Us </strong>you will find helpful information about the Historic Preservation Commission (HPC), its policies, activities, membership and history.</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;">A subcatogory in About Us, <strong>Design Review,</strong> is directed to persons who wish to help maintain the historic ambience of their property or own historic property in Historic Districts. Here you will find the guidelines and the requirements for making changes in their structures.</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;"> </span></p>\r\n<hr />\r\n<p>&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: medium;"><span style="font-family: mceinline;">&nbsp;</span><strong>History</strong> is divided into <strong>two sections</strong>. One deals with printed sources about the town &ndash; texts and documents including a brief introduction and overview of the city from the beginning, understanding our official Historic Districts, a presentation of how the town grew, and a working copy of the latest revision of, <em>The Mount Vernon Centennial Book</em> (with index) (now available&nbsp;<a href="bookindex.php">here</a>). If you are searching for ancestors you may want to check the <em>Mt Vernon Cemetery Records</em> &ndash; plots and maps (coming in November 2011)</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;">The second section of the <strong>History</strong> is an extensive <strong>collection of historic images </strong>&ndash; many following the 1880&rsquo;s when studio photographers were in abundance in Mt Vernon partly because of college students ordering graduation photos, post cards, etc. The Introduction to these images explains the very simple system for finding specific photos. You are<strong> invited to share your knowledge</strong> about any picture by a simple process.</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<hr />\r\n<p>&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: medium;">Our history is <strong>linked to the story of Cornell College</strong>. <a href="links.php">Click here</a><strong> </strong>to visit our links, which point to the college and the College Archives as well as city, county, state, and federal history and preservations organizations and resources.</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: medium;">Mount Vernon Historic Preservation Commission :: Mount Vernon, Iowa</span></p>'),
(2, 'history', ''),
(3, 'about', '<p style="text-align: left;"><span style="font-size: large;">Mount Vernon Historic Preservation Commission</span></p>\r\n<p style="text-align: left;"><span style="font-size: medium;">Duties: identify, interpret and protect the historic and archaeological resources of our community and encourage historic tourism.</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: small;">Members: Commission Members as of March 2011</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kay Johansen, Chair</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Richard H. Thomas, Vice Chair</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Edward Sauter</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Guy Booth</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hugh Lifson</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Leah Rogers</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pat Westercamp</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Carine Klein</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Paul Robinson </span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pat Westercamp</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Susan Hargus </span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Imran Farooqi</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Lawson (Skip) Clark</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: small;"><strong>Meetings:</strong> Open to the public</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; First Saturday of each month at 9:15a.m.</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Visitors Center</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><strong>Current Projects: &nbsp; &nbsp;&nbsp;</strong></span></p>\r\n<p style="text-align: left;"><span style="font-size: small;">&nbsp;</span><span style="font-size: small;"><span style="white-space: pre;"> </span>Walking Tours</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Educational Workshop &ndash; Preservation Issues</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Survey of older barns and early garages</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Preservation of Local Mural</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>City Records Preservation</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>City Heritage Days (history program)</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Cemetery Records Preservation</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Opportunities for Volunteers</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Summer student Internship</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: small;"><strong>CONTACT US:</strong></span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Mount Vernon City Hall </span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>213 1<sup>st</sup> St. SW.</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Mount Vernon, IA&nbsp; 52314</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Chair: Kay Johansen</span></p>\r\n<p style="text-align: left;">&nbsp;</p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Main Street Iowa &ndash; Joe Jennison</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Visitors Center</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>311 1st Street&nbsp; NW</span></p>\r\n<p style="text-align: left;"><span style="font-size: small;"><span style="white-space: pre;"> </span>Mt Vernon, IA&nbsp; 52314</span></p>'),
(4, 'districts', '<p>Here is an introduction to the historic districts of Mt. Vernon, Iowa.</p>\r\n<p>&nbsp;</p>\r\n<p><a href="search.php?terms=ash+park+district">Ash Park District</a><br /> <a href="search.php?terms=brick+district">Brick District</a><br /> <a href="search.php?terms=cornell+district">Cornell District</a><br /> <a href="search.php?terms=commerical+district">Commerical District</a></p>'),
(5, 'history_text', '<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/how_mount_vernon_grew.pdf" target="_blank">How Mount Vernon Grew</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/history_intro_to_mount_vernon.pdf" target="_blank">Introduction to the History of Mount Vernon</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/Survey_of_Brick_Buildings.pdf" target="_blank">Survey of Brick Buildings</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="bookindex.php">Centennial Book</a></span></p>\r\n<p style="text-align: center;">&nbsp;</p>'),
(6, 'history_images', '<p style="text-align: center;"><span style="font-size: large;">Please use the seach bar in the upper right to search historical images.</span></p>'),
(7, 'design_review', '<p style="text-align: left;"><span style="font-size: small;">You will find here all the information needed to secure approval for proposed changes in structures located in Historic District. We included helpful GUIDELINE for making improvements or changes in older homes not included in an historic district but by age and style enhance the historic fabric of the community. The commission encourages consultations with us as early a possible in the design process so that any confusions can be clarified before final plans are submitted.</span></p>\r\n<p style="text-align: center;">&nbsp;</p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/design_review.pdf" target="_blank">Design Review</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/design_review_flowchart.pdf" target="_blank">Design Review Flowchart</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/design_review_process.pdf" target="_blank">Design Review Process</a></span></p>\r\n<p style="text-align: center;"><span style="font-size: large;"><a href="http://www.mvhpc.org/data/design_review_q_and_a.pdf" target="_blank">Design Review Q and A</a></span></p>\r\n<p style="text-align: center;">&nbsp;</p>'),
